def add_questions():
    """–î–æ–±–∞–≤–∏—Ç—å –≤–æ–ø—Ä–æ—Å—ã –≤ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö"""
    try:
        conn = config.get_db_connection()
        cursor = conn.cursor()
        
        logger.info(f"–î–æ–±–∞–≤–ª–µ–Ω–∏–µ {len(NEW_QUESTIONS)} –Ω–æ–≤—ã—Ö –≤–æ–ø—Ä–æ—Å–æ–≤...")
        
        added_count = 0
        for question, answer, keywords, category in NEW_QUESTIONS:
            # –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —É–∂–µ —Ç–∞–∫–æ–π –≤–æ–ø—Ä–æ—Å
            cursor.execute("SELECT id FROM faq WHERE question = %s", (question,))
            if cursor.fetchone():
                logger.warning(f"–í–æ–ø—Ä–æ—Å —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç: {question[:50]}...")
                continue
            
            # –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤—ã–π –≤–æ–ø—Ä–æ—Å
            cursor.execute('''
                INSERT INTO faq (question, answer, keywords, category)
                VALUES (%s, %s, %s, %s)
            ''', (question, answer, keywords, category))
            
            logger.info(f"‚úÖ –î–æ–±–∞–≤–ª–µ–Ω: {question[:50]}...")
            added_count += 1
        
        conn.commit()
        conn.close()
        
        logger.info(f"üéâ –£—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–æ {added_count} –≤–æ–ø—Ä–æ—Å–æ–≤!")
        return True
        
    except Exception as e:
        logger.error(f"‚ùå –û—à–∏–±–∫–∞: {e}", exc_info=True)
        return False

if __name__ == "__main__":
    add_questions()
